import {useState} from 'react';

const useForm = initialValues => {
    // On passe initialValues en tant que props p/ que le useState est une valeur initial
    const [values, setValues] = useState(initialValues);
    // on retourne un tablues qui contient la value, ainsi que la méthode setValue qui va prendre un object, qui 
    return [values, e => setValues({...values, [e.target.name] : e.target.value})];
};

export default useForm
