import React, { useState } from 'react';
import './App.css';


const App = () => {
  const [{count, count2}, setCount] = useState({count : 10, count2 : 20});

  return <div>
  <button onClick={() => setCount(currentState => ({
    count: currentState.count + 1, ...currentState}))}>+</button>
  {count}
  {count2}
  </div>
}

export default App;
