import React, { useState } from 'react';
import './App.css';


const App = () => {
  const [{count, count2}, setCount] = useState({count : 10, count2 : 20});

  return <div>
  <button onClick={() => setCount(currentState => ({
    ...currentState,
    count: currentState.count + 1,
    })
    )}>+
    </button>
    <p>{count}</p>
    <p></p>


  </div>
}

export default App;
