import React, { useState, useCallback } from "react";
import { Hello } from "./Hello";

const App = () => {
    const [count, setCount] = useState(0);
    const increment = useCallback(() => {
        setCount(count )
    }, [])
    return(
        <div>
        <Hello increment={() => setCount(count + 1)}/>
        <div>count : {count}</div>
        </div>
    );
};

export default App
