import { useEffect, useState } from 'react';

export const useFetch = url => {
    const [state, setState] = useState({data: null, loading: true});

    useEffect( () => {
        const f = async () => {
            let link = await fetch(url)
            setState({data: link.y, loading : false})
        } 
        f()
    }, [url])

    return state
}
