import { useState } from 'react';
import axios from 'axios'

const useFetch = (url => {
    const [state, setState] = useState({data: null, loading: true});

    React.useEffect = (() => {
            const fetchdata = async () => {
                const result = await axios(url);
                const txt  = result;
                setState(txt.text)
            };
            fetchdata()
        }, [url]);
        return state
});


export default useFetch
