import React, { useReducer, useState } from 'react';


const reducer = (state, action) => {
  switch(action.type){
    case 'increment':
      return state + 1
    case 'decrement':
      return state - 1;
    default:
      return state;
  }
}

const App = () => {

  const [{todos}, dispatch] = useReducer(reducer, { todos: [] });
  const [text, setText] = useState

  return(
    <div>
    <form onSubmit>
      <input value={text} onChange={e => setText(e.target.value)}/>
    </form>
    </div>
  );
};

export default App;
