import React, { useReducer } from 'react';


const reducer = (state, action) => {
  switch(action.type){
    case 'increment':
      return state + 1
    case 'decrement':
      return state - 1;
    default:
      return state;
  }
}

const App = () => {

  const [count, dispatch] = useReducer(reducer, 0);

